package producer

import (
	"github.com/Shopify/sarama"
	"github.com/yueja/cf-kafka/version"
	"testing"
	"time"
)

var bigString = ""

var address = "192.168.0.124:9092"

func TestInitSyncProducer(t *testing.T) {
	type args struct {
		addresses []string
		version   *version.Version
	}
	tests := []struct {
		name    string
		args    args
		wantErr bool
	}{
		{
			name: "test_01",
			args: args{
				addresses: []string{"192.168.0.124:9092"},
				version:   &version.Version{Major: 4},
			},
			wantErr: true,
		},
		{
			name: "test_02",
			args: args{
				addresses: []string{"192.168.0.124:9092"},
				version:   &version.Version{Major: 1},
			},
			wantErr: false,
		},
		{
			name: "test_03",
			args: args{
				addresses: []string{},
				version:   &version.Version{Major: 1},
			},
			wantErr: true,
		},
		{
			name: "test_04",
			args: args{
				addresses: []string{},
				version:   &version.Version{Major: 1},
			},
			wantErr: true,
		},
	}
	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			if tt.name == "test_04" {
				if err := InitSyncProducer(nil); (err != nil) != tt.wantErr {
					t.Errorf("InitSyncProducer() error = %v, wantErr %v", err, tt.wantErr)
				}
			} else {
				if err := InitSyncProducer(&Config{
					Addresses:       tt.args.addresses,
					MaxMessageBytes: 0,
					Version:         tt.args.version,
				}); (err != nil) != tt.wantErr {
					t.Errorf("InitSyncProducer() error = %v, wantErr %v", err, tt.wantErr)

				}
			}
		})
	}
}

func TestSyncProducer_SendEvent(t *testing.T) {
	type fields struct {
		producer sarama.SyncProducer
	}
	type args struct {
		event []*sarama.ProducerMessage
	}
	_ = InitSyncProducer(&Config{
		Addresses:       []string{address},
		MaxMessageBytes: 100,
	})
	tests := []struct {
		name    string
		fields  fields
		args    args
		wantErr bool
	}{
		{
			name: "test_01",
			fields: fields{
				producer: GetSyncProducer().producer,
			},
			args: args{
				event: []*sarama.ProducerMessage{
					{
						Topic: "test_topic01",
						Headers: []sarama.RecordHeader{
							{
								Key:   []byte("key"),
								Value: []byte("value"),
							},
						},
						Value:     sarama.ByteEncoder("测试消息"),
						Timestamp: time.Now(),
					},
				},
			},
			wantErr: false,
		},
		{
			name: "test_02",
			fields: fields{
				producer: GetSyncProducer().producer,
			},
			args: args{
				event: []*sarama.ProducerMessage{
					{
						Topic: "test_topic01",
						Headers: []sarama.RecordHeader{
							{
								Key:   []byte("key"),
								Value: []byte("value"),
							},
						},
						Value:     sarama.ByteEncoder(bigString),
						Timestamp: time.Now(),
					},
				},
			},
			wantErr: true,
		},
	}
	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			p := &SyncProducer{
				producer: tt.fields.producer,
			}
			if err := p.SendEvent(tt.args.event...); (err != nil) != tt.wantErr {
				t.Errorf("SendEvent() error = %v, wantErr %v", err, tt.wantErr)
			}
		})
	}
}

